    private void registerCustomer() {
        String mobile = jTextField1.getText();
        String fname = jTextField3.getText();
        String lname = jTextField2.getText();

        if (mobile.isEmpty()) {
            JOptionPane.showMessageDialog(this, "Please enter Mobile Number", "Warning", JOptionPane.ERROR_MESSAGE);

        } else if (!mobile.matches("\\d{10}")) {
            JOptionPane.showMessageDialog(this, "Mobile should contain 10 digits", "Warning", JOptionPane.WARNING_MESSAGE);

        } else if (fname.isEmpty()) {
            JOptionPane.showMessageDialog(this, "Please enter First Name", "Warning", JOptionPane.WARNING_MESSAGE);

        } else if (lname.isEmpty()) {
            JOptionPane.showMessageDialog(this, "Please enter Last Name", "Warning", JOptionPane.WARNING_MESSAGE);

        } else {

            try {
                MYSQL.iud("INSERT INTO `customer` VALUES ('" + mobile + "','" + fname + "','" + lname + "','0')");
                JOptionPane.showMessageDialog(this, "Register Succcessfull !!!", "Success", JOptionPane.INFORMATION_MESSAGE);
                Cashier cashier = new Cashier();
                
                if(refreshCallback != null) {
                    refreshCallback.run();
                }
                
            } catch (Exception e) {
                e.printStackTrace();
            }
        }
    }


